
public class CastingExTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		double doubleNum = 21.318d;
		int n = 0;
		
		n = (int)doubleNum;
		
		System.out.println(n);
		System.out.println();
		
//		doubleNum = (double)n;
		doubleNum = n;
		
		System.out.println(doubleNum);
		System.out.println();
		
		
		
	}

}
