import java.util.Scanner;

public class ForFlowEx8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count = 0;
		int num = 4;
		
		while(count <= 4) {
			System.out.println(num + " - I can do it");
			
			num--; // 출력에 대한 숫자
			count++; // 실행횟수
		}
		
		
		
		
	}

}
